export class UiNotificationModule {
  static forRoot(): ModuleWithProviders {
    return {
      ngModule: UiNotificationModule,
      providers: [
        {
          provide: APP_INITIALIZER,
          useFactory: uiNotificationInitFactory,
          deps: [KitOverlayService],
          multi: true,
        },
      ],
    };
  }
}

