    box-shadow: 0 1px 5px rgba(0, 0, 0, .1);
    outline: none;
  }
  &.-has-errors {
    border-color: #ff0c09;
    &:hover, &:focus {
      border-color: #ff0c09;
    }
  }
}

:host {
  clip: rect(0 0 0 0);
  clip-path: inset(100%);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  position: absolute;
  width: 1px;
}

$boxSize: 40px;
$borderWidth: 1px;

.toggle {
  cursor: pointer;
  margin-left: $boxSize + 8px;
  position: relative;
  &::before {
    background-color: #ffffff;
    border: $borderWidth solid #becad6;
    border-radius: $boxSize;
    box-shadow: none;
    content: "";
    display: block;
    height: $boxSize / 2;
    left: 0;
    margin-left: - $boxSize - 8px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    transition: all 250ms cubic-bezier(.27, .01, .38, 1.06);
    width: $boxSize;
  }
  &::after {
    background: #becad6;
    border-radius: ($boxSize / 2 - 4px) / 2;
    content: "";
    height: $boxSize / 2 - 4px;
    left: $borderWidth + 2px;
    margin-left: - $boxSize - 8px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    transition: all 300ms cubic-bezier(.27, .01, .38, 1.06);
    width: $boxSize / 2 - 4px;
  }
  &.-checked {
    &::before {
      background: #2399e5;
      border-color: #2399e5;
    }
    &::after {
      background: #ffffff;
      margin-left: - $boxSize / 2 - 8px;
    }
    &:active {
      &::after {
        margin-left: - $boxSize / 2 - 12px;
      }
    }
  }
  &.-focused {
    &::before {
      border-color: #2399e5;
      box-shadow: 0 1px 5px rgba(0, 0, 0, .1);
    }
  }
  &.-has-errors {
    &::before {
      border-color: #ff0c09;
    }
    &.-checked::before {
      background: #ff0c09;
    }
  }
  &.-disabled {
    cursor: default;
    &.-checked {
      &::before {
        background: #9da8b4;
        border-color: #9da8b4;
      }
      &::after {
        background: #ffffff;
      }
    }
    &::before {
      background: #f6f6f6;
      border-color: #becad6;
    }
  }
  &:active {
    &::after {
      width: $boxSize / 2;
    }
  }
}

span {
  border: 1px solid #bbbbbb;
  display: inline-block;
  margin: 4px;
  padding: 8px 16px;
}

$offset: 2px;

span {
  border-radius: 3px;
  box-shadow: 0 0 6px 0 rgba(50, 50, 50, .1);
  padding: 4px 8px;
  &.color {
    &-default {
      background: rgba(0, 0, 0, .6);
      color: #ffffff;
    }
    &-primary {
      background: rgba(35, 153, 229, .8);
      color: #ffffff;
    }
  }
  &.position {
    &-top-center, &-top-left, &-top-right {
      margin-bottom: $offset;
    }
    &-right-center, &-right-top, &-right-bottom {
      margin-left: $offset;
    }
    &-bottom-center, &-bottom-left, &-bottom-right {
      margin-top: $offset;
    }
    &-left-center, &-left-top, &-left-bottom {
      margin-right: $offset;
    }
  }
}

:host {
  border-bottom: 1px solid #eeeeee;
  display: block;
  &:last-child {
    border-bottom: 0;
  }
}

.title {
  cursor: pointer;
  display: flex;
  flex-direction: row;
  padding: 8px 16px;
  .wrapper {
    flex-grow: 1;
  }
  .arrow {
    background: transparent;
    border: 0;
  }
}

:host {
  border-bottom: 1px solid #eeeeee;
  color: #333333;
  cursor: pointer;
  display: block;
  padding: 8px 16px;
  text-decoration: none;
  &:last-child {
    border-bottom: 0;
  }
}

:host {
  display: block;
  overflow: hidden;
}

.wrapper {
  background: #f3f3f3;
}

:host {
  background: #fbfbfb;
  border: 1px solid #eeeeee;
  display: block;
}
