import { Directive, EventEmitter, OnInit, Output } from '@angular/core';
import { KitIntersectionService } from '../kit-intersection.service';

/**
 * Observe viewport intersection.
 *
 *
 * ### Usage
 *
 * And listen to intersection changes:
 *
 * ```html
 * <div (kitIntersection)="visible = $event">
 * </div>
 * ```
 */
@Directive({
  selector: '[kitIntersection]',
  providers: [
    KitIntersectionService,
  ],
})
export class KitIntersectionDirective implements OnInit {
  @Output() kitIntersection = new EventEmitter<boolean>();

  constructor(
    private intersection: KitIntersectionService,
  ) {
  }

  ngOnInit() {
    this.intersection.observe().subscribe(isIntersecting => {
      this.kitIntersection.emit(!!isIntersecting);
    });
  }
}

import { Observable, Subject } from 'rxjs';
import { uuid } from '../util/uuid';
import { KitLoadingEndFn, KitLoadingState } from './meta';

export class KitLoadingProgress {

  private current = new Set<string>();

  private _state: KitLoadingState = KitLoadingState.None;

  private _stateChanges = new Subject<KitLoadingState>();

  constructor(public readonly id: string) {
  }

  get state(): KitLoadingState {
    return this._state;
  }

  get stateChanges(): Observable<KitLoadingState> {
    return this._stateChanges.asObservable();
  }

  start(key?: string): KitLoadingEndFn {
    this.current.add(key || uuid());
    this.checkState();
    return () => {
      this.end(key);
    };
  }

  end(key?: string) {
    if (key) {
      this.current.delete(key);
    } else {
      this.current.clear();
    }
    this.checkState();
  }

  private checkState() {
    if (this.current.size > 0 && this._state === KitLoadingState.None) {
      this.setState(KitLoadingState.InProgress);
    }
    if (this.current.size === 0 && this._state === KitLoadingState.InProgress) {
      this.setState(KitLoadingState.None);
    }
  }

  private setState(state: KitLoadingState) {
    this._state = state;
    this._stateChanges.next(this._state);
  }
}

