          top: '0',
        };
      case 'bottom':
        return {
          ...common,
          alignItems: 'flex-start',
          flexDirection: 'row',
          height: this.px(field.height - anchor.bottom),
          left: this.px(anchor.left),
          top: this.px(anchor.bottom),
          width: this.px(anchor.width),
        };
      case 'bottom-center':
        return {
          ...common,
